/*
 * Realizar un programa que muestre los números del 1 al 100 utilizando la instrucción for
 */

package Ejercicios;

public class Ejercicio1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i = 1; i <= 100; i++) {
			System.out.println(i);
		}
	}

}
