/*
 * Realizar un programa que muestre los números del 1 al 100 utilizando la instrucción for
 */

package Ejercicios;

public class Ejercicio01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		final int TOPE = 100;

		for (int i = 1; i <= TOPE; i++) {
			System.out.print(i + " ");
		}
	}

}
